function Lineaire(Lin,yDel,y,t)%Paramétrage de la sinusoide du mode Démo if(Lin == 0) Fs = 10;                          %Echantillons par seconde dt = 1/Fs;                        %Temps par échantillons td = (0:dt:10);                   %Variable t yd = sin(pi/2*td);                %Variable y (sinus choisi) Techantillon = 10; endif%Paramétrage du signal du mode projet if(Lin == 1) tailleYd = size(yDel); yd = yDel; dt = 1/(1000); td = t; Techantillon = 1; endiftailleYd = size(yd);  for a = 1: tailleYd(2);                        %pour chaque point du signal 1  posYd = a+a-1;                                 %Les valeurs ajoutées par l'interpolation sont rangées dans les cases paires du tableau  y2d(posYd) = yd(a);                            %Les valeurs de y du signal de base sont déplacées dans les cases impaires du tableau    if a<tailleYd(2)    y2d(posYd+1) = (yd(a)+yd(a+1))/2;            %Dans les cases paires du nouveau tableau, on range la moyenne de la case du tableau de base correspondante avec la suivante    endif  endfor  t2d = 0:dt/2:Techantillon;                       %On double la fréquence d'échantillonage, on divise donc le temps par échantillon par 2plot(td,yd,'ob')                                 %Affichage du signal de basehold on plot(t2d,y2d,'xr')                               %Affichage du signal sur-échantillonétitle('Signal en fonction du temps');            %Titrexlabel('Temps (en seconde)');                    %Abscissesylabel('Amplitude');                             %ordonnéesendfunction